mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY DATE(o.odate)) AS No,  o.oid, o.o_pid, p.pname, o.oqty, o.oprice, o.o_cid, c.cname, CONCAT(c.cpostnum,' ', c.caddress1, c.caddress2) as caddress, c.cphone, o.odate
FROM product AS p, ordering AS o, customer AS c 
WHERE p.pid=o.o_pid AND c.cid=o.o_cid AND o.odate >= #{startday} AND o.odate <= #{endday}

SELECT ROW_NUMBER() OVER (ORDER BY DATE(o.odate)) AS No,  o.oid, o.o_pid, p.pname, o.oqty, o.oprice, o.o_cid, c.cname, CONCAT(c.cpostnum,' ', c.caddress1, c.caddress2) as caddress, c.cphone, o.odate 
FROM product AS p, ordering AS o, customer AS c 
WHERE p.pid=o.o_pid AND c.cid=o.o_cid AND c.cid = #{cid} AND o.odate >= #{startday} AND o.odate <= #{endday}
